//};
class Counter extends Component{
  incrementHandler(){
    this.props.increment();
  }
  increaseHandler(){
    this.props.increase()
  }
  decrementHandler(){
    this.props.decrement();
  }
  toggleCounterHandler(){
    this.props.toggle()
  }
  render(){
return(<main className={classes.counter}>
      <h1>Redux Counter</h1>
      {this.toggleCounterHandler.state && <div className={classes.value}>{this.props.counter}</div>}
      <div>
      <button onClick={this.incrementHandler.bind(this)}>Increment</button>
      <button onClick={this.increaseHandler.bind(this)}>Increase by 10</button>
      <button onClick={this.decrementHandler.bind(this)}>Decrement</button>
      </div>
      <button onClick={this.toggleCounterHandler.bind(this)}>Toggle Counter</button>
    </main>)
  }
}
const mapStateToProps = state =>{
  return{
    counter:state.counter,
    show:state.showCounter
  }
}

const mapDispatchToProps = dispatch =>{
  return{
    increment:()=>dispatch({type:'increment'}),
    increase:()=>dispatch({type:'increase',amount:10}),
    decrement:()=>dispatch({type:'decrement'}),
    toggle:()=>dispatch({type:'toggle'}),
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Counter);
